module.exports.run = async function({ api, event, Users }) {
    const threadID = event.threadID;
    const path = __dirname + "/cache/shayari_index.json";

    let indexData = {};
    if (fs.existsSync(path)) {
        try {
            indexData = JSON.parse(fs.readFileSync(path, 'utf-8'));
        } catch (error) {
            console.error("Error reading shayari index file:", error);
            indexData = {}; 
        }
    }

    let currentIndex = indexData[threadID] || 0;

    if (currentIndex >= shayaris.length) {
        currentIndex = 0;
    }

    api.sendMessage(shayaris[currentIndex], threadID);

    indexData[threadID] = currentIndex + 1;
    fs.writeFileSync(path, JSON.stringify(indexData));
};
